import './App.css';
const cardNumbers = [1,2,3,4,5,6,7,8];
function App(){
  const [cards, setCards] = useState([])
  const [turns, setTurns] = useState(0)
  // suffle card
  const suffleCards = () => {
    const shuffleCards = [...cardNumbers, ...cardNumbers]
    .sort(() => Math.random() - 0.5)
    .map((card) =>({...card, id: Math.random()}))

    setCards(suffleCards)
    setTurns(0)
  }
  console.log(cards, turns)
  return(
    <div className='App'>
      <h1>Memory Game</h1>
      <button onClick={suffleCards}>New Game</button>
    </div>
  )
}
export default App;
