import React, { useState } from 'react';


let deck = [];
for(let i=1; i<=8; i++){
    deck.push(i);
    deck.push(i);
}
deck = deck.sort(() => Math.random() - 0.5);
function App(){
  const [cards, setCards] = useState([])
  const [turns, setTurns] = useState(0)
  // suffle card
  const suffleCards = () => {


    setCards(deck)
    setTurns(0)
  }
  console.log(cards, turns)
  return(
    <div className='App'>
      <h1>Memory Game</h1>
      <button onClick={suffleCards}>New Game</button>
    </div>
  )
}
export default App;
